package com.woory.backend;

import static org.assertj.core.api.Assertions.*;
import static org.junit.jupiter.api.Assertions.*;
import static org.mockito.Mockito.*;

import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.List;
import java.util.UUID;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;
import org.junit.jupiter.params.provider.ValueSource;
import org.mockito.MockedStatic;
import org.springframework.web.multipart.MultipartFile;

import com.woory.backend.error.CustomException;
import com.woory.backend.error.ErrorCode;
import com.woory.backend.utils.Base64ImageConverter;
import com.woory.backend.utils.ImageConverter;
import com.woory.backend.utils.LinkImageConverter;
import com.woory.backend.utils.PhotoUtils;

public class ImageConvertersTest {
	List<ImageConverter> imageConverters = List.of(new Base64ImageConverter(), new LinkImageConverter());

	@ParameterizedTest(name = "방식 : {0}")
	@CsvSource(value = {
		"Base64 data:image/png;base64,",
		"Base64 data:image/jpeg;base64,",
		"URL https://cdn.pixabay.com/photo/2023/02/18/11/22/cactus-7797750_1280.jpg",
		"URL https://cdn.pixabay.com/photo/2015/10/01/17/17/car-967387_1280.png",
		"URL https://freepngimg.com/thumb/christmas_tree/3-2-christmas-tree-png-file.png"
	}, delimiter = ' ')
	void 이미지_형식_지원_테스트(String format, String image) {
		assertTrue(imageConverters.stream()
			.anyMatch(imageConverter -> imageConverter.supports(image)));
	}

	@ParameterizedTest(name = "방식 : {0}")
	@CsvSource(value = {
		"Base64 data:text/html,%3Ch1%3EHello%2C%20World%21%3C%2Fh1%3E",
		"Base64 data:text/plain;base64,SGVsbG8sIFdvcmxkIQ==",
		"URL https://test.test.com/photo/test.csv",
		"URL https://test.test.com/photo/test.pdf",
		"URL https://freepngimg.com/thumb/test.txt"
	}, delimiter = ' ')
	void 이미지_형식이_맞지_않음(String type, String image) {
		assertFalse(imageConverters.stream()
			.anyMatch(imageConverter -> imageConverter.supports(image)));
	}

	@ParameterizedTest
	@ValueSource(strings = {
		"data:image/png;base64,",
		"data:image/jpeg;base64,",
	})
	void Base64ImageConverter로_변환한_파일과_PhotoUtils로_변환한_파일이_같다(String image) throws IOException {
		UUID value = UUID.randomUUID();
		MockedStatic<UUID> uuid = mockStatic(UUID.class);
		when(UUID.randomUUID()).thenReturn(value);

		MultipartFile converted1 = imageConverters.stream()
			.filter(imageConverter -> imageConverter.supports(image))
			.findFirst()
			.map(imageConverter -> imageConverter.convert(image))
			.orElseThrow(() -> new CustomException(ErrorCode.ERROR_SAVING_FILE));

		MultipartFile converted2 = PhotoUtils.base64ToMultipartFile(image);

		assert converted1 != null;
		assert converted2 != null;

		assertThat(converted1.getName()).isEqualTo(converted2.getName());
		assertThat(converted1.getBytes()).isEqualTo(converted2.getBytes());
		assertThat(converted1.getSize()).isEqualTo(converted2.getSize());
		uuid.close();
	}

	@ParameterizedTest
	@ValueSource(strings = {
		"https://cdn.pixabay.com/photo/2023/02/18/11/22/cactus-7797750_1280.jpg",
		"https://cdn.pixabay.com/photo/2015/10/01/17/17/car-967387_1280.png",
		"https://freepngimg.com/thumb/christmas_tree/3-2-christmas-tree-png-file.png"
	})
	void LinkImageConverter로_변환한_파일과_PhotoUtils로_변환한_파일이_같다(String image) throws IOException {
		UUID value = UUID.randomUUID();
		MockedStatic<UUID> uuid = mockStatic(UUID.class);
		when(UUID.randomUUID()).thenReturn(value);

		MultipartFile converted1 = imageConverters.stream()
			.filter(imageConverter -> imageConverter.supports(image))
			.findFirst()
			.map(imageConverter -> imageConverter.convert(image))
			.orElseThrow(() -> new CustomException(ErrorCode.ERROR_SAVING_FILE));

		MultipartFile converted2 = PhotoUtils.urlToFile(image);

		assert converted1 != null;
		assert converted2 != null;

		assertThat(converted1.getName()).isEqualTo(converted2.getName());
		assertThat(converted1.getBytes()).isEqualTo(converted2.getBytes());
		assertThat(converted1.getSize()).isEqualTo(converted2.getSize());
		uuid.close();
	}

	@ParameterizedTest(name = "방식 : {0}, 확장자 : {1}")
	@CsvSource(value = {
		"Base64 PNG data:image/png;base64,",
		"Base64 JPEG data:image/jpeg;base64,",
		"URL JPG https://cdn.pixabay.com/photo/2023/02/18/11/22/cactus-7797750_1280.jpg",
		"URL PNG https://cdn.pixabay.com/photo/2015/10/01/17/17/car-967387_1280.png",
		"URL PNG https://freepngimg.com/thumb/christmas_tree/3-2-christmas-tree-png-file.png"
	}, delimiter = ' ')
	void ImageConverter로_변환한_파일을_읽을_수_있다(String type, String format, String image) throws IOException {
		MultipartFile converted = imageConverters.stream()
			.filter(imageConverter -> imageConverter.supports(image))
			.findFirst()
			.map(imageConverter -> imageConverter.convert(image))
			.orElseThrow(() -> new CustomException(ErrorCode.ERROR_SAVING_FILE));

		assert converted != null;
		Path output = Files.createTempFile("temp", "webp").toAbsolutePath();
		converted.transferTo(output);
		File file = new File(output.toString());
		assertThat(file).canRead();
		output.toFile().delete();
	}
}
