package com.woory.backend;

import static org.assertj.core.api.Assertions.*;
import static org.junit.jupiter.api.Assertions.*;

import java.io.File;
import java.io.IOException;
import java.io.InputStream;
import java.net.URL;
import java.nio.file.Files;
import java.nio.file.Path;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;
import org.junit.jupiter.params.provider.ValueSource;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.web.multipart.MultipartFile;

import com.woory.backend.error.CustomException;
import com.woory.backend.error.ErrorCode;
import com.woory.backend.utils.PhotoUtils;

public class PhotoUtilsTest {
	private static final Logger log = LoggerFactory.getLogger(PhotoUtilsTest.class);
	String base64ImageSvgConvertedToPng = "data:image/png;base64,";
	String urlImage = "https://upload.wikimedia.org/wikipedia/commons/thumb/9/95/Font_Awesome_5_brands_github.svg/220px-Font_Awesome_5_brands_github.svg.png";

	@Test
	void webp포맷이_크기가_더_작다_with_base64() throws IOException {
		byte[] orgImgBytes = base64ImageSvgConvertedToPng.split(",")[1].getBytes();
		MultipartFile converted = PhotoUtils.base64ToMultipartFile(base64ImageSvgConvertedToPng);
		assert converted != null;
		byte[] convertedBytes = converted.getBytes();
		log.info("원본 파일 크기 : {}", orgImgBytes.length);
		log.info("변환 파일 크기 : {}", convertedBytes.length);
		assertTrue(convertedBytes.length < orgImgBytes.length);
	}

	@Test
	void webp포맷이_크기가_더_작다_with_URI() throws IOException {
		byte[] orgImgBytes;
		try (InputStream is = new URL(urlImage).openStream()) {
			orgImgBytes = is.readAllBytes();
		}
		MultipartFile converted = PhotoUtils.urlToFile(urlImage);
		assert converted != null;
		byte[] convertedBytes = converted.getBytes();
		log.info("원본 파일 크기 : {}", orgImgBytes.length);
		log.info("변환 파일 크기 : {}", convertedBytes.length);
		assertTrue(convertedBytes.length < orgImgBytes.length);
	}

	@ParameterizedTest(name = "확장자 : {0}")
	@CsvSource(value = {
		"PNG data:image/png;base64,",
		"JPEG data:image/jpeg;base64,"
	}, delimiter = ' ')
	void Base64를_변환_후_파일을_읽을_수_있다(String format, String base64) throws IOException {
		MultipartFile converted = PhotoUtils.base64ToMultipartFile(base64);
		assert converted != null;
		Path output = Files.createTempFile("temp", "webp").toAbsolutePath();
		converted.transferTo(output);
		File file = new File(output.toString());
		assertThat(file).canRead();
		output.toFile().delete();
	}

	@ParameterizedTest(name = "확장자 : {0}")
	@CsvSource(value = {
		"JPG https://cdn.pixabay.com/photo/2023/02/18/11/22/cactus-7797750_1280.jpg",
		"PNG https://cdn.pixabay.com/photo/2015/10/01/17/17/car-967387_1280.png",
		"PNG https://freepngimg.com/thumb/christmas_tree/3-2-christmas-tree-png-file.png"
	}, delimiter = ' ')
	void URI를_변환_후_파일을_읽을_수_있다(String message, String urlImage) {
		assertDoesNotThrow(() -> PhotoUtils.urlToFile(urlImage));
	}

	@ParameterizedTest
	@ValueSource(strings = {
		"data:application/pdf;base64,",
		"data:text/plain;base64,7YWM7Iqk7Yq47YyM7J287J6F64uI64ukLgo="})
	void 이미지_파일_외에는_오류가_발생한다(String base64File) {
		assertThatThrownBy(() -> PhotoUtils.base64ToMultipartFile(base64File))
			.isInstanceOf(CustomException.class)
			.hasMessage(ErrorCode.FILE_IS_NOT_IMAGE.getMessage());
	}
}
